    e << static_cast< const ::xml_schema::type& > (i);

    // action_code
    //
    {
      ::xercesc::DOMAttr& a (
        ::xsd::cxx::xml::dom::create_attribute (
          L"action_code",
          e));

      a << i.action_code ();
    }

    // anim_name
    //
    {
      ::xercesc::DOMAttr& a (
        ::xsd::cxx::xml::dom::create_attribute (
          L"anim_name",
          e));

      a << i.anim_name ();
    }

    // effect_name
